INSERT INTO public.cls_course_category (name, description) VALUES
                                                               ('Компьютерная грамотность', 'Понятные уроки по основам работы с компьютером: включение/выключение, работа с мышью и клавиатурой, создание файлов и папок. Идеально для начинающих без предварительного опыта.'),
                                                               ('Финансовая грамотность', 'Обучение безопасному управлению пенсией, использованию банковских карт, мобильного банкинга и защите от финансовых мошенников. Практические советы по бюджету.'),
                                                               ('Здоровье и активное долголетие', 'Комплексная программа по поддержанию физического здоровья: упражнения для суставов, правильное питание, профилактика возрастных заболеваний и первая помощь.'),
                                                               ('Цифровая безопасность', 'Важные знания по защите личных данных в интернете: создание надежных паролей, распознавание фишинговых сайтов, безопасные онлайн-покупки и защита от телефонных мошенников.'),
                                                               ('Социальные сети и общение', 'Освоение популярных мессенджеров (WhatsApp, Viber) и социальных сетей (Одноклассники) для общения с родными и друзьями, обмена фото и видео.'),
                                                               ('Мобильные устройства', 'Подробное изучение смартфонов и планшетов: настройка, основные приложения, использование камеры, голосовые сообщения и видеозвонки для комфортного общения.'),
                                                               ('Государственные услуги онлайн', 'Обучение использованию портала Госуслуг: запись к врачу, получение справок, оплата ЖКХ и других услуг без очередей и лишних хлопот.'),
                                                               ('Творчество и хобби', 'Курсы по цифровому творчеству: создание фотоальбомов, видеомонтаж семейных роликов, основы цифровой фотографии и онлайн-посещение музеев.'),
                                                               ('Правовая грамотность', 'Права и льготы пенсионеров: оформление документов, защита прав потребителей, наследственные вопросы и взаимодействие с государственными органами.'),
                                                               ('Психологическое благополучие', 'Техники борьбы со стрессом и одиночеством, поддержание когнитивных функций, упражнения для памяти и внимания, основы медитации и релаксации.');
INSERT INTO public.reg_course (title, description, avatar_url, id_course_category) VALUES
                                                                                       ('Компьютерная грамотность: от нуля до уверенного пользователя',
INSERT INTO public.cls_lesson (id_course, title, video_url, lecture_text, lesson_order) VALUES
                                                                                            (1, 'Знакомство с компьютером: первые шаги', '/videos/computer-basics-1.mp4', 'В этом уроке мы изучим основные компоненты компьютера: системный блок, монитор, клавиатуру и мышь. Узнаем, как правильно включать и выключать компьютер, познакомимся с рабочим столом и основными элементами интерфейса. Практические упражнения помогут освоить базовые действия.', 1),
                                                                                            (1, 'Работа с мышью и клавиатурой', '/videos/computer-basics-2.mp4', 'Осваиваем управление компьютером: учимся уверенно работать с мышью (движение, клики, прокрутка) и клавиатурой (ввод текста, горячие клавиши). Практикуемся в открытии программ и навигации по меню.', 2),
                                                                                            (1, 'Создание и сохранение файлов', '/videos/computer-basics-3.mp4', 'Учимся создавать папки для организации файлов, сохранять документы и находить их later. Изучаем разные типы файлов и программы для их открытия. Практика: создаем личную папку с документами.', 3),
                                                                                            (1, 'Основы работы в интернете', '/videos/computer-basics-4.mp4', 'Знакомство с браузером: как открывать сайты, пользоваться поиском Google/Yandex, сохранять закладки. Безопасность в сети: какие сайты можно trust, а какие лучше avoid.', 4),
                                                                                            (1, 'Электронная почта: регистрация и использование', '/videos/computer-basics-5.mp4', 'Создаем личный email аккаунт, учимся отправлять и получать письма, прикреплять файлы. Практикуем общение по email с родными и друзьями.', 5);
INSERT INTO public.cls_lesson (id_course, title, video_url, lecture_text, lesson_order) VALUES
                                                                                            (2, 'Банковская карта: правила безопасности', '/videos/finance-1.mp4', 'Изучаем виды банковских карт, PIN-коды, правила хранения и использования. Как защититься от skimming и что делать при утере карты.', 1),
                                                                                            (2, 'Мобильный банкинг: безопасное использование', '/videos/finance-2.mp4', 'Устанавливаем и настраиваем мобильное приложение банка. Учимся проверять баланс, переводить деньги и оплачивать услуги. Защита от мошенников.', 2),
                                                                                            (2, 'Распознавание финансовых мошенников', '/videos/finance-3.mp4', 'Типичные схемы обмана: звонки от "банка", SMS с просьбой перевести деньги, fake лотереи. Учимся проверять информацию и не поддаваться на уговоры.', 3),
                                                                                            (2, 'Онлайн-платежи: безопасные покупки', '/videos/finance-4.mp4', 'Как безопасно оплачивать товары и услуги онлайн. Выбираем надежные сайты, защищаем данные карты. 3D-Secure и другие методы защиты.', 4),
                                                                                            (2, 'Пенсионные накопления и льготы', '/videos/finance-5.mp4', 'Управление пенсионными средствами, получение льгот и субсидий онлайн. Официальные порталы и сервисы для пенсионеров.', 5);
INSERT INTO public.cls_lesson (id_course, title, video_url, lecture_text, lesson_order) VALUES
                                                                                            (3, 'Утренняя гимнастика для суставов', '/videos/health-1.mp4', 'Комплекс безопасных упражнений для разминки суставов и мышц. Упражнения выполняются сидя и standing с опорой. Регулярность и умеренность - ключ к успеху.', 1),
                                                                                            (3, 'Правильное питание после 60', '/videos/health-2.mp4', 'Особенности metabolism в зрелом возрасте. Продукты для здоровья костей, сердца и мозга. Простые и полезные рецепты.', 2),
                                                                                            (3, 'Профилактика возрастных заболеваний', '/videos/health-3.mp4', 'Регулярные medical check-ups, контроль давления и сахара. Раннее выявление проблем и своевременное обращение к врачам.', 3),
                                                                                            (3, 'Психологическое здоровье и общение', '/videos/health-4.mp4', 'Борьба с одиночеством, поддержание social connections. Техники релаксации и позитивного мышления.', 4),
                                                                                            (3, 'Первая помощь в бытовых ситуациях', '/videos/health-5.mp4', 'Базовые навыки first aid: что делать при падениях, высоком давлении, обострениях chronic diseases. Телефоны экстренных служб.', 5);
INSERT INTO public.cls_lesson (id_course, title, video_url, lecture_text, lesson_order) VALUES
                                                                                            (4, 'Создание надежных паролей', '/videos/security-1.mp4', 'Учимся создавать сложные пароли, которые легко запомнить. Использование password managers. Защита accounts от взлома.', 1),
                                                                                            (4, 'Распознавание phishing атак', '/videos/security-2.mp4', 'Как отличить настоящее письмо от мошеннического. Проверка email адресов и ссылок. Что никогда не спрашивают в банках.', 2),
                                                                                            (4, 'Безопасные онлайн-покупки', '/videos/security-3.mp4', 'Признаки надежных интернет-магазинов. Защита bank card данных при оплате. Потребительские права в digital space.', 3),
                                                                                            (4, 'Защита личных данных в соцсетях', '/videos/security-4.mp4', 'Настройки privacy в социальных сетях. Какая информация не должна быть public. Защита от digital stalkers.', 4),
                                                                                            (4, 'Антивирусная защита и обновления', '/videos/security-5.mp4', 'Выбор и установка antivirus программ. Важность регулярных updates. Сканирование компьютера на вирусы.', 5);
INSERT INTO public.cls_lesson (id_course, title, video_url, lecture_text, lesson_order) VALUES
                                                                                            (5, 'WhatsApp: установка и настройка', '/videos/social-1.mp4', 'Скачиваем и устанавливаем WhatsApp. Регистрация по номеру телефона. Basic настройки профиля и уведомлений.', 1),
                                                                                            (5, 'Общение в WhatsApp: сообщения и звонки', '/videos/social-2.mp4', 'Отправка текстовых и голосовых сообщений. Звонки и video calls. Создание group chats для семьи.', 2),
                                                                                            (5, 'Одноклассники: регистрация и поиск друзей', '/videos/social-3.mp4', 'Создаем аккаунт в Одноклассниках. Ищем старых друзей и одноклассников. Настраиваем privacy settings.', 3),
                                                                                            (5, 'Обмен фото и видео в соцсетях', '/videos/social-4.mp4', 'Как загружать и просматривать фотоальбомы. Отправка видео родным. Создание family memories онлайн.', 4),
                                                                                            (5, 'Безопасность в социальных сетях', '/videos/social-5.mp4', 'Как избежать scams в соцсетях. Настройки конфиденциальности. Block нежелательных контактов.', 5);
INSERT INTO public.cls_lesson (id_course, title, video_url, lecture_text, lesson_order) VALUES
                                                                                            (6, 'Основы работы со смартфоном', '/videos/phone-1.mp4', 'Знакомство с интерфейсом Android/iOS. Основные жесты и настройки. Управление контактами и звонки.', 1),
                                                                                            (6, 'Полезные приложения для здоровья', '/videos/phone-2.mp4', 'Приложения для измерения давления, шагомера, напоминания о лекарствах. Медицинские трекеры.', 2),
                                                                                            (7, 'Регистрация на портале Госуслуг', '/videos/gov-1.mp4', 'Пошаговая регистрация и верификация аккаунта. Получение digital signature.', 1),
                                                                                            (8, 'Создание цифрового фотоальбома', '/videos/creative-1.mp4', 'Перенос старых фото в digital формат. Создание онлайн-альбомов для семьи.', 1),
                                                                                            (9, 'Права потребителей: защита от обмана', '/videos/legal-1.mp4', 'Основные consumer rights. Как вернуть некачественный товар. Образцы претензий.', 1),
                                                                                            (10, 'Техники релаксации и медитации', '/videos/wellbeing-1.mp4', 'Простые breathing упражнения для снятия stress. Mindfulness практики для daily life.', 1);
